import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

/*
Display a list of movies where each movie contains a list of users that favorited it.

For detailed instructions, refer to instructions.md.
*/

const profiles = [
  {
    id: 1,
    userID: '1',
    favoriteMovieID: '1',
  },
  {
    id: 2,
    userID: '2',
    favoriteMovieID: '1',
  },
  {
    id: 3,
    userID: '4',
    favoriteMovieID: '5',
  },
  {
    id: 4,
    userID: '5',
    favoriteMovieID: '2',
  },
  {
    id: 5,
    userID: '3',
    favoriteMovieID: '5',
  },
  {
    id: 6,
    userID: '6',
    favoriteMovieID: '4',
  },
];

const users = {
  1: {
    id: 1,
    name: 'Jane Jones',
    userName: 'coder',
  },
  2: {
    id: 2,
    name: 'Matthew Johnson',
    userName: 'mpage',
  },
  3: {
    id: 3,
    name: 'Autumn Green',
    userName: 'user123',
  },
  4: {
    id: 3,
    name: 'John Doe',
    userName: 'user123',
  },
  5: {
    id: 5,
    name: 'Lauren Carlson',
    userName: 'user123',
  },
  6: {
    id: 6,
    name: 'Nicholas Lain',
    userName: 'user123',
  },
};

const movies = {
  1: {
    id: 1,
    name: 'Planet Earth',
  },
  2: {
    id: 2,
    name: 'Selma',
  },
  3: {
    id: 3,
    name: 'Million Dollar Baby',
  },
  4: {
    id: 4,
    name: 'Forrest Gump',
  },
  5: {
    id: 5,
    name: 'Get Out',
  },
};

class App extends Component {
  render() {
      /****************************************************************/
      /* Build an array of movie titles                               */
      var moviesArray = [];
      profiles.map(profile => {
        const userId = profile.userID;
        const movieId = profile.favoriteMovieID;
        var movieFans = {};        
      })
      const filmFans = {
        1: {tile:"Forest Gump", fans:[]},
        2: {tile:"Forest Gump", fans:[]},
        1: {tile:"Forest Gump", fans:[]},
        1: {tile:"Forest Gump", fans:[]},
        1: {tile:"Forest Gump", fans:[]},
        1: {tile:"Forest Gump", fans:[]},
      }
    return (
      /****************************************************************/
      /* Build the page using React.creatElement                      */
      React.createElement('div', {className:'App'}, 
		React.createElement('header', {className:'App-header'},
          React.createElement('img', {src: logo, className:'App-logo', alt:'logo'}, null),
          React.createElement('h1', {className:'App-title'}, 'ReactND - AJB - Coding Practice'),
        ),
        React.createElement('div', {className:'App-content', style: {backgroundColor:'#333399', padding:'2em'}},
          React.createElement('h2',{style: {color:'#aaaaaa'}},'How popular is MY favorite movie? (using React.createElement)'),       
          /* You can't iterate (directly) over a JavaScript hash, so */
          React.createElement('ul', {},
            profiles.map(profile => {
              const movieId = profile.favoriteMovieID;
              const movieTitle = movies[movieId].name;
              const movieUserId = profile.userID;
              const userName = users[movieUserId].name;
              return(
                React.createElement( 'li', {style: {color:'#aaaaaa', padding:'0.5em'}}, `${movieId} - ${movieTitle} - ${movieUserId}:${userName}`,
                )
              )
            })
          )
        ),
        React.createElement('div', {className:'App-content', style: {backgroundColor:'#339933', padding:'2em'}},
          React.createElement('h2',{style: {color:'#000000'}},'List of movies and people who like them:')
          /*
          React.createElement('ul', {},
            moviesArray.map(movie => {
              return(
                React.createElement( 'li', {style: {color:'#aaaaaa', padding:'0.5em'}}, `${movie}`)
              )
            })
            */
        )
      )      /* END OF: React.createElement('div', {className:'App', */
      /* End of build the page with react.createApp                   */
      /****************************************************************/
      /****************************************************************
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">ReactND - Coding Practice</h1>
        </header>
        <h2>How Popular is Your Favorite Movie?</h2>
      </div>
      ****************************************************************/
    )      /* END OF: return */
  };      /* END OF: render */
}      /* END OF: class App extends Component */

export default App;
